import React, { useState } from 'react';
import Button from '../../Components/Button';
import * as S from './style';

const Queue = () => {
  const [status, setStatus] = useState('Escolha sua fila');
  const [normalPassword, setNormalPassword] = useState(0);
  const [preferentialPassword, setPreferentialPassword] = useState(0);
  const updatePassword = (password) => {
    console.log('o que é password', password);
    if (password === 'normal') {
      const normalUpdatePassword = normalPassword + 1;
      setNormalPassword(normalUpdatePassword);
      setStatus(`Sucesso! Sua fila é ${password} e sua senha é: ${normalUpdatePassword}`);
      return localStorage.setItem('normal', normalUpdatePassword);
    } else {
    const preferentialUpdatePassword = preferentialPassword + 1;
    setPreferentialPassword(preferentialUpdatePassword);
    console.log('tá passando aqui?')
    setStatus(`Sucesso! Sua fila é ${password} e sua senha é: ${preferentialUpdatePassword}`);
    return localStorage.setItem('preferential', preferentialUpdatePassword)
    };
  };
  // useEffect(() => {
  //   const MaxTime = 2500;
  //   setTimeout(() => setStatus(''), MaxTime);
  // }, [status]);
  return (
    <>
      <S.Title>
        { status }
      </S.Title>
      <Button
        value="normal"
        onClick={ ({target}) => updatePassword(target.value) }
      >
        Normal
      </Button>
      <Button
        value="preferential"
        onClick={ ({target}) => updatePassword(target.value) }
      >
        Preferencial
      </Button>
    </>
  )
};

export default Queue;
